import React from "react";
import { MdCheckBox, MdCheckBoxOutlineBlank } from "react-icons/md";

// import { TfiCheck, TfiCheckBox } from "react-icons/tfi";

import "./Todo.css";
const Todo = ({ todo, onCheckToggle, onInsertToggle }) => {
  const { text, checked, id } = todo;
  return (
    <div className="Todo">
      <div className={`content ${checked ? "checked" : ""}`}>
        {checked ? (
          <MdCheckBox
            onClick={() => {
              onCheckToggle(id);
            }}
          />
        ) : (
          <MdCheckBoxOutlineBlank
            onClick={() => {
              onCheckToggle(id);
            }}
          />
        )}
        <div className="text">
          {" "}
          {text}
          onInsertToggle()
        </div>
        <button>수정</button>
        <button>삭제</button>
      </div>
    </div>
  );
};

export default Todo;
