import React, { useState } from "react";

const Todo = ({ todo, deleteTodo, editTodo }) => {
  // const { id, isDone, todoTitle } = todo; 구조분해할당

  const [editOpen, setEditOpen] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const startEdit = () => {
    setEditOpen(true);
    setInputValue(todo.todoTitle);
  };

  const onChangeInputValue = (e) => {
    setInputValue(e.target.value);
  };

  const endEdit = () => {
    const data = inputValue;
    editTodo(todo.id, data);
  };

  return (
    <div>
      <h3>{todo.todoTitle}</h3>
      <p>{todo.isDone.toString()}</p>
      {editOpen && <input value={inputValue} onChange={onChangeInputValue} />}
      {editOpen ? (
        <button
          onClick={() => {
            endEdit();
          }}
        >
          확인
        </button>
      ) : (
        <button onClick={startEdit}>수정</button>
      )}
      <button
        onClick={() => {
          deleteTodo(todo.id);
        }}
      >
        삭제
      </button>
    </div>
  );
};

export default Todo;
