import React from "react";
import { v4 as uuidv4 } from "uuid";

const Todo = ({ todos, setTodos }) => {
  const onClickDelete = () => {
    setTodos((prev) => {
      prev.filter((deleteItem) => {
        return deleteItem.id == todos.id;
      });
    });
  };

  return (
    <>
      {/* <div>{todos.ischecked ? "체크됨" : "체크안됨"}</div> */}

      {todos.map((item) => {
        return (
          <div key={item.id}>
            {item.ischecked}
            {item.text}
            <button>수정</button>
            <button onClick={onClickDelete}>삭제</button>
          </div>
        );
      })}
    </>
  );
};

export default Todo;
